import keyboard
from selenium.webdriver.common.by import By
from selenium import webdriver
from time import sleep

driver = webdriver.Chrome()
driver.get('https://orteil.dashnet.org/cookieclicker/')

sleep(5)
botao_lingua = driver.find_element(By.ID, 'langSelect-EN')
botao_lingua.click()
sleep(5)

opcoes = driver.find_element(By.ID, 'prefsButton')
opcoes.click()

importar_save = driver.find_elements(By.CLASS_NAME[3], 'option smallFancyButton')
importar_save.click()

chave_save = '%3D%3D%21END%21'
keyboard.press_and_release(chave_save)
keyboard.press('enter')
sleep(5)
botao = driver.find_element(By.ID,'bigCookie')
while not keyboard.is_pressed('1'):
    botao.click()
    sleep(0.0005)
    if(keyboard.is_pressed('2')):
        sleep(10)